import { useCallback, useContext } from 'react';
import MenuContext from '../../../Menu/MenuContext';
const useDrilldownMenuItem = props => {
  const {
    key,
    menu,
    onMouseEnter
  } = props;
  const {
    activeItemKey,
    onItemMouseEnter,
    onAwayClick
  } = useContext(MenuContext);
  const isOpened = key === activeItemKey;
  const handleItemMouseEnter = useCallback(event => {
    if (onMouseEnter) {
      onMouseEnter(event);
    }
    if (onItemMouseEnter) {
      onItemMouseEnter(key, menu);
    }
  }, [key, menu, onMouseEnter, onItemMouseEnter]);
  const handleAwayClick = useCallback(() => {
    if (onAwayClick) {
      onAwayClick();
    }
  }, [onAwayClick]);
  return {
    isOpened,
    onItemMouseEnter: handleItemMouseEnter,
    onAwayClick: handleAwayClick
  };
};
export default useDrilldownMenuItem;
